import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'
class NoteList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            notes: [],
        }
        this.handlePin=this.handlePin.bind(this)
    }
    componentDidMount() {
        axios.get('http://localhost:3006/notes')
            .then(response => {
                this.setState(() => ({ notes: response.data }))
            })
    }
    handlePin(id) {
        console.log(id)
        axios.put(`http://localhost:3006/notes/${id}`, {isPinned:true})
            .then(response => {
                let note
                note = this.state.notes.find(note => note._id === response.data._id)
                note.isPinned = response.data
                this.setState(() => {})
            })             
    }
    render() {
        return (
            <div>
                <h1>Listing notes   - {this.state.notes.length}</h1>
                <ul>
                    {this.state.notes.map(note => <li key={note._id}><Link to={`/notes/${note._id}`}>{note.title}</Link><button onClick={() => {this.handlePin(note._id)}}>Pin</button></li>)}
                </ul>

                <Link to='/notes/new'>Add Note</Link>
            </div>
        )
    }
}
export default NoteList
