import React from 'react'
import CategoryForm from './Form1'
import axios from 'axios'


class CategoryEdit extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            category:{}
        }
    }
    componentDidMount(){
        const id=this.props.match.params.id
        axios.get(`http://localhost:3006/categories/${id}`)
            .then(response => {
                this.setState(() => ({category:response.data}))
            })
    }
    handleSubmit = (formData) => {
        axios.put(`http:/localhost:3006/categories/${this.state.category._id}`,formData)
        .then(response => {
            if(response.data.hasOwnProperty('errors')){
                console.log(response.data.errors)
            }else{
                this.props.history.push(`/categories/${this.props.match.params.id}`)
            }
        })

    }
    render(){
        return(
            <div>
                <h2>Edit this Category</h2>
                <CategoryForm category={this.state.category} handleSubmit={this.handleSubmit}/>
            </div>
        )
    }
}
export default CategoryEdit
