import React from 'react'
import axios from 'axios'

class ShowCategory extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            category:{},
            notes:[]
        }
    }
    componentDidMount(){
        const id = this.props.match.params.id
        console.log(id)
        axios.get(`http://localhost:3006/categories/${id}`)
        .then(response => {
            this.setState(() => ({category:response.data}))
            axios.get(`http://localhost:3006/notes`)
                .then(response => {
                    console.log(response.data)
                    this.setState(() => ({ notes: response.data }))
                })
        })

    }
    handleRemove = () => {
        axios
    }
    render(){
        const notes = this.state.notes.filter(note => note.category === this.state.category._id)
        console.log(notes)
        return(
        <div>
            <h2>{this.state.category.name}</h2>
                {notes.map(note => {
                    return <span><p>{note.title}</p><p>{note.body}</p></span>
                })}
            <button onClick={this.handleRemove}>Delete</button>
        </div>
        )
    }
}

export default ShowCategory
