import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'
class NoteList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            notes: [],
        }
        // this.handlePin=this.handlePin.bind(this)
        // this.handleUnPin = this.handleUnPin.bind(this)
        // this.handleCopy=this.handleCopy.bind(this)
        // this.handleDelete=this.handleDelete.bind(this)
    }
    componentDidMount() {
        axios.get('http://localhost:3006/notes', {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
            .then(response => {
                this.setState(() => ({ notes: response.data }))
            })
    }
    handlePin = (id)  => {
        axios.put(`http://localhost:3006/notes/${id}`, { isPinned: true }, {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
            .then(response => {
                        const notes = this.state.notes.map(note => {
                            if (note._id === response.data._id) {
                                note.isPinned = response.data.isPinned
                                return note
                            } else {
                                return note
                            }
                        })
                        this.setState(() => ({ notes }))
                    })             
    }
    handleUnPin = (id) => {
        axios.put(`http://localhost:3006/notes/${id}`, { isPinned: false }, {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
            .then(response => {
                const notes=this.state.notes.map(note => {
                     if (note._id === response.data._id) {
                        note.isPinned = response.data.isPinned
                        return note
                    }else{
                        return note
                    }
                })
                console.log(notes)
                this.setState(() => ({notes}))
            })
    }

    handleCopy = (note) => {
        let newNote
        if(note){
            newNote = {
                title:note.title,
                body:note.body,
                category:note.category,
                tags:note.tags.map(tag => ({tag:tag.tag})),
                isPinned:note.isPinned
            }
        }
        axios.post('http://localhost:3006/notes', newNote , {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
        .then(response => {
            console.log(response.data)
            this.setState((prevState) => ({notes:[...prevState.notes,response.data]}))
        })
    }
    handleDelete = (id) => {
        axios.delete(`http://localhost:3006/notes/${id}`, {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
        .then(response => {
            // const note= this.state.notes.find(noteItem => noteItem._id === id)
            // const index = this.state.notes.indexOf(note)
            // this.state.notes.splice(index,1)
            this.setState((prevState) => ({notes:prevState.notes.filter(note => note._id !== id)}))
        })
    }
    render() {
        const pinNotes = this.state.notes.filter(note => note.isPinned === true)
        const UnpinNotes = this.state.notes.filter(note => note.isPinned === false)
        return (
            <div>
                {this.state.notes.length !== 0 && (<h1>Listing notes   - {this.state.notes.length}</h1>)}

                {pinNotes.length !== 0 && (<span><h2>pinned list</h2></span>)}
                <ul>
                    {pinNotes.map(note => <li key={note._id}><Link to={`/notes/${note._id}`}>{note.title}</Link><button onClick={() => { this.handleUnPin(note._id) }}>Unpin</button><button onClick={() => {
                        this.handleDelete(note._id)
                    }}>delete</button><button onClick={() => {
                        this.handleCopy(note)}}>copy</button></li>)}
                </ul>

                {UnpinNotes.length !== 0 && (<span><h2>unpinned list</h2></span>)}
                <ul>
                    {UnpinNotes.map(note => <li key={note._id}><Link to={`/notes/${note._id}`}>{note.title}</Link><button onClick={() => { this.handlePin(note._id) }}>pin</button><button onClick={() => {
                        const confirmRemove = window.confirm('Are you sure?')
                        if(confirmRemove){
                            this.handleDelete(note._id)
                        }
                    }}>delete</button><button onClick={() => {
                        this.handleCopy(note)}}>copy</button></li>)}
                </ul>

                <Link to='/notes/new'>Add Note</Link>
            </div>
        )
    }
}
export default NoteList
