const reducerInitialNotes = []
const NotesReducer = ( state=reducerInitialNotes, action) => {
        switch(action.type){
            case 'SET_NOTE':{
                return [...state, action.payload]
            }
            case 'REMOVE_NOTE':{
                const notes = state.filter(note => note._id !== action.payload)
                return notes
            }
            case 'GET_NOTES': {
                return [...state]
            }
            case 'CHANGE_NOTE_ISPINNED':{
                const notes = state.map(note => {
                    if(note._id === action.payload._id){
                        note.isPinned = action.payload.isPinned
                        return note
                    }else{
                        return note
                    }
                })
                    return notes
            }
            default : {
                return[...state]
            }
        }
}
export default NotesReducer
