import React from 'react'
import {BrowserRouter, Route, Link, Switch} from 'react-router-dom'

import NoteList from './List'
import NoteShow from './Show'
import AddNote from './New'
import NoteEdit from './Edit'
import CategoriesList from './CategoryList'
import ShowCategory from './ShowCategory'
import AddCategory from './NewCategory'
import CategoryEdit from './EditCategory'

import RegistrationUser from '../users/Registration'
import LoginUser from '../users/Login'
import Account from '../users/Account'
import LogoutUser from '../users/Logout'

class App extends React.Component{
    constructor(){
        super()
        this.state = {
            isAuthentication:false
        }
    }
    componentDidMount(){
        if(localStorage.getItem('userAuthToken')){
            this.setState({isAuthentication:true})
        }
    }
    handleAuth=(bool) => {
        this.setState({isAuthentication:bool})
    }
    render(){
        console.log(this.state.isAuthentication)
        return(
            <BrowserRouter>
                <div className='container'>
                    {/* <h1>My Note App</h1> */}

                    {/* <Link to='/notes'>List Notes</Link><pre>
                    <Link to='/categories'>List categories</Link></pre> */}
                    <ul>
                        {this.state.isAuthentication &&
                            (<div>
                                <li><Link to='/users/account' >Account</Link></li>
                                <li><Link to='/users/logout'>Logout</Link></li>
                            </div>)
                        }

                        {!this.state.isAuthentication &&
                            (<div>
                            <li><Link to='/users/register'>Register</Link></li>
                            <li><Link to='/users/login'>Login</Link></li>
                            </div>)
                        }
                    </ul>

                    {/* Route Matcher */}
                    <Switch>
                        <Route exact path='/notes' component={NoteList} />
                        <Route path='/notes/:id' component={NoteShow} exact={true} />
                        <Route exact path='/notes/new' component={AddNote} />
                        <Route exact path='/notes' component={NoteList} />
                        <Route exact path='/notes/new' component={AddNote} />
                        <Route path='/notes/:id' component={NoteShow} exact={true}/>
                        <Route exact path='/notes/edit/:id' component={NoteEdit} />
                        <Route exact path='/categories' component={CategoriesList}/>
                        <Route exact path='/categories/new' component={AddCategory} />
                        <Route path='/categories/:id' component={ShowCategory} exact={true}/>
                        <Route path='/categories/edit/:id' component={CategoryEdit} exact={true} />

                        <Route path='/users/register' component={RegistrationUser} exact={true} />
                        <Route path='/users/logout' render={(props) => {
                            return <LogoutUser {...props} handleAuth={this.handleAuth} exact={true} />
                        }} />
                        <Route path='/users/login' render={(props) => {
                            return <LoginUser {...props} handleAuth={this.handleAuth} exact={true} />
                        }} />

                        <Route path='/users/account' component={Account} exact={true} />

                    </Switch>
                </div>
            </BrowserRouter>
            )
    }
}
export default App
