import React from 'react'
import axios from '../../config/config'
import {Link} from 'react-router-dom'

class ShowCategory extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            category:{},
            notes:[]
        }
    }
    componentDidMount(){
        const id = this.props.match.params.id
        console.log(id)
        axios.get(`/categories/${id}`, {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
        .then(response => {
            this.setState(() => ({category:response.data,notes:response.data.notes}))

        })

    }
    handleRemove = () => {
        const id = this.props.match.params.id
        axios.delete(`/categories/${id}`, {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
        .then(response => {
            const confirm = window.confirm('Are you Sure?')
            if(confirm){
                this.props.history.push('/categories')
            }
        })
    }
    render(){
        // const notes = this.state.notes.filter(note => note.category === this.state.category._id)
        return(
        <div>
            <h2>{this.state.category.name}</h2>
            <ul> note - {this.state.notes.map(note => {
                        return note.note && <li key={note._id}><span><p>{note.note.title}</p><p>{note.note.body}</p></span></li>
                    })}
            </ul>

            <Link className="Link" to={`/notes/`}>Back</Link>
            <Link className="Link"to={`/categories/edit/${this.props.match.params.id}`}>Edit</Link>
            <button onClick={this.handleRemove}>Delete</button>
        </div>
        )
    }
}

export default ShowCategory
