import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

class ShowCategory extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            category:{},
            notes:[]
        }
    }
    componentDidMount(){
        const id = this.props.match.params.id
        console.log(id)
        axios.get(`http://localhost:3006/categories/${id}`)
        .then(response => {
            this.setState(() => ({category:response.data}))
            axios.get(`http://localhost:3006/notes`)
                .then(response => {
                    console.log(response.data)
                    this.setState(() => ({ notes: response.data }))
                })
        })

    }
    handleRemove = () => {
        const id = this.props.match.params.id
        axios.delete(`http://localhost:3006/categories/${id}`)
        .then(response => {
            if(response.data.hasOwnProperty('errors')){
                console.log(response.data.errors)
            }else{
                this.props.history.push('/categories')
            }
        })
    }
    render(){
        const notes = this.state.notes.filter(note => note.category === this.state.category._id)
        return(
        <div>
            <h2>{this.state.category.name}</h2>
            <ul>
                    {notes.map(note => {
                        return <li key={note._id}><span><p>{note.title}</p><p>{note.body}</p></span></li>
                    })}
            </ul>

            <Link to='/categories'>Back</Link>
            <Link to={`/categories/edit/${this.props.match.params.id}`}>Edit</Link>
            <button onClick={this.handleRemove}>Delete</button>
        </div>
        )
    }
}

export default ShowCategory
