import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

class CategoryList extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            categories:[]
        }
    }
    componentDidMount(){
        axios.get('http://localhost:3006/categories', {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
        .then(response => {
            this.setState(() => ({categories:response.data}))
        })
    }
    render(){
        return(
            <div>
                <h2>Listing Category - {this.state.categories.length}</h2>
                <ul>
                    {this.state.categories.map(category => <li key={category._id}><Link to={`/categories/${category._id}`}>{category.name}</Link></li>)}
                </ul>
                <Link className="Link" to='/categories/new'>Add Category</Link>
            </div>
        )
    }
}
export default CategoryList
