import React from 'react'

class FormLogin extends React.Component{
    constructor(){
        super()
        this.state = {
            email:'',
            password:''
        }
    }
    handleChange = (e) => {
        const value =  e.target.type === 'text' ? e.target.value : e.target.value
        const name = e.target.name

        this.setState(() => ({
            [name]:value
        }))
    }
    handleSubmit = (e) => {
        e.preventDefault()
        const formData = {
            email:this.state.email,
            password:this.state.password
        }
        this.props.handleLogin(formData)
    }
    render(){
        return(
            <div>
                <h2>Login of user</h2>
                <form onSubmit={this.handleSubmit} >
                    <div className="form-group">
                        <label for="exampleInputEmail">Email</label>
                        <input type='email' className="form-control" name='email' value={this.state.email} onChange={this.handleChange} />
                        <small class="form-text text-muted">don't enter incorrect email</small>
                    </div>

                    <br />
                    <div className="form-group">
                        <label for="exampleInputPassword1">Password</label>
                        <input type='password' name='password' value={this.state.password} onChange={this.handleChange} />
                    </div>

                    <br />
                    <input type='submit' value='Submit' />
                </form>
            </div>
        )
    }
}
export default FormLogin
