import React from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'

class NoteShow extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            note: {}
        }
        this.handleRemove=this.handleRemove.bind(this)
    }
    componentDidMount(){
        const id = this.props.match.params.id
        axios.get(`http://localhost:3006/notes/${id}`, {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
        .then(response => {
            this.setState(() => ({note:response.data}))
        })
    }
    handleRemove(e){
        const id = this.props.match.params.id

                const confirm = window.confirm('Are you sure?')
                if(confirm){
                    axios.delete(`http://localhost:3006/notes/${id}`, {
                        headers: {
                            'x-auth': localStorage.getItem('userAuthToken')
                        }
                    })
                    .then(() => {
                      this.props.history.push('/notes')  
                    })
                }
    }
    handleRemoveTag=(tag) => {
        const id = this.props.match.params.id
        axios.delete(`http://localhost:3006/notes/removeTag?noteId=${id}&tagId=${tag}`)
        .then(response => {
                this.setState(() => ({
                    note:response.data
                }))
        })
    }
    render(){
        return(
            <div>
                {this.state.note.title && (<h2>{this.state.note.title}</h2>)}
                {this.state.note.body && (<p>{this.state.note.body}</p>)}
                {this.state.note.category && (<p>category-{this.state.note.category.name}</p>)}
                {this.state.note.tags && (<pre>tags - { 
                    this.state.note.tags.map(tag =>  <ul>
                        <li key={tag._id}><span>{tag.tag.name}<button onClick={this.handleRemoveTag}>X</button></span></li>
                                 </ul>
                        )}</pre>)}
                <Link to='/notes'>back</Link>

                <Link to={`/notes/edit/${this.props.match.params.id}`}>Edit</Link >
                <button onClick={this.handleRemove}>Delete</button>
            </div>
        )
    }
}
export default NoteShow
