import React from 'react'
import Axios from 'axios'


class Form extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            title:'',
            body:'',
            category:'',
            categories:[],
            tags:[],
            selectedTags:[]
        }
        this.handleTitle=this.handleTitle.bind(this)
        this.handleBody=this.handleBody.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    handleTitle(e){
        const title=e.target.value
        this.setState(() => ({title}))
    }
    handleBody(e){
        const body=e.target.value
        this.setState(() => ({body}))
    }
    handleSubmit(e){
        e.preventDefault()
        const select =  this.state.selectedTags.map(tag => {
            return {
                tag:tag._id
            }
        })
        const formData = {
            title:this.state.title,
            body:this.state.body,
            category:this.state.category,
            tags:select

        }
        console.log(formData)
        this.props.handleSubmit(formData)

    }
    handleSelect = (e) => {
        const category = e.target.value
        this.setState(() => ({category}))
    }

    componentDidMount(){
        Axios.get('http://localhost:3006/categories',{
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
        .then(response => {
                this.setState(() => ({categories:response.data}))
        })
        Axios.get('http://localhost:3006/tags', {
            headers: {
                'x-auth': localStorage.getItem('userAuthToken')
            }
        })
            .then(response => {
                this.setState(() => ({ tags: response.data }))
            })
    }
    componentWillReceiveProps(nextProps) {
        console.log(nextProps)
        this.setState(() => ({
            title: nextProps.note.title,
            body: nextProps.note.body,
            category: nextProps.note.category._id,
            tags:nextProps.note.tags.map(tag => tag.tag)
        }))
    }
    handleSelectedTag=(tag) => {
        this.setState(prevState => ({ selectedTags: [...prevState.selectedTags,tag]}))

    }
    render(){
        console.log(this.state.selectedTags)
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        category:
                        <select value={this.state.category} name='category' onChange={this.handleSelect}>
                            <option value=''>select</option>
                            {this.state.categories.map(category => <option key={category._id} value={category._id}>{category.name}</option>)}
                            </select>
                    </label><br />

                    <label>
                        Title:
                        <input type='text' value={this.state.title} onChange={this.handleTitle}/> 
                    </label>
                    <br />
                    <label>
                        Body:
                        <input type='text' value={this.state.body} onChange={this.handleBody} />
                    </label><br /><br/>

                    <lable>
                        tags:<br />
                        {this.state.tags.map(tag => <label key={tag._id}><input type='checkbox' checked={tag.tag} onClick={() => {
                            this.handleSelectedTag(tag)
                        }}/>{tag.name}<br /></label>)}
                    </lable><br />

                    <input type="submit" value="SUBMIT" />
                </form>
            </div>
        )
    }
}

export default Form
