import React from 'react'
import NoteForm from './Form'
import axios from 'axios'

class NoteEdit extends React.Component{
    constructor(props){
        console.log('edit note constructor')
        super(props)
        this.state = {
            note:{}
        }
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    componentDidMount(){
        console.log('cdm - get note information')
        const id = this.props.match.params.id
        axios.get(`http://localhost:3006/notes/${id}`)
        .then(response => {
            this.setState(() => ({note:response.data}))
        })
    }
    handleSubmit(formData){
        axios.put(`http://localhost:3005/notes/${this.state.note._id}`,formData)
        .then(response => {
            if(response.data.hasOwnProperty('errors')){
                console.log(errors)
            }else{
                this.props.history.push(`/notes/${this.state.note._id}`)
            }
        })
    }
    render(){
        return(
            <div>
                <h2>Edit the Note</h2>
                <NoteForm note={this.state.note} handleSubmit={this.handleSubmit} />
            </div>

        )
    }
}
export default NoteEdit
