import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

class CategoryList extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            categories:[]
        }
    }
    componentDidMount(){
        axios.get('http://localhost:3006/categories')
        .then(response => {
            this.setState(() => ({categories:response.data}))
        })
    }
    render(){
        return(
            <div>
                <h2>Listing Category - {this.state.categories.length}</h2>
                <ul>
                    {this.state.categories.map(category => <li><Link to={`${category._id}`}>{category.name}</Link></li>)}
                </ul>
            </div>
        )
    }
}
export default CategoryList
