import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'
class NoteList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            notes: [],
        }
        this.handlePin=this.handlePin.bind(this)
        this.handleUnPin = this.handleUnPin.bind(this)
        this.handleCopy=this.handleCopy.bind(this)
        this.handleDelete=this.handleDelete.bind(this)
    }
    componentDidMount() {
        axios.get('http://localhost:3006/notes')
            .then(response => {
                this.setState(() => ({ notes: response.data }))
            })
    }
    handlePin(id) {
        axios.put(`http://localhost:3006/notes/${id}`, {isPinned:false})
            .then(response => {
                let note
                note = this.state.notes.find(note => note._id === response.data._id)
                note.isPinned = response.data.isPinned
                console.log(this.state.notes)
                this.setState(() => ({}))
            })             
    }
    handleUnPin(id) {
        axios.put(`http://localhost:3006/notes/${id}`, { isPinned: true })
            .then(response => {
                const note = this.state.notes.find(note => note._id === response.data._id)
                note.isPinned = response.data.isPinned
                console.log(this.state.notes)
                this.setState(() => ({}))
            })
            console.log(this.state.notes)
    }
    handleCopy(note){
        console.log(note)
        axios.post('http://localhost:3006/notes',note)
        .then(response => {
            this.state.notes.push(response.data)
            // this.state.notes.push(response)
            this.setState(() => ({}))
        })
    }
    handleDelete(id){
        axios.delete(`http://localhost:3006/notes/${id}`)
        .then(response => {
            // const note= this.state.notes.find(noteItem => noteItem._id === id)
            // const index = this.state.notes.indexOf(note)
            // this.state.notes.splice(index,1)
            this.setState((prevState) => ({notes:prevState.notes.filter(note => note._id !== id)}))
        })
    }
    render() {
        const pinNotes = this.state.notes.filter(note => note.isPinned === true)
        const UnpinNotes = this.state.notes.filter(note => note.isPinned === false)
        return (
            <div>
                <h1>Listing notes   - {this.state.notes.length}</h1>

                <ul>
                    {pinNotes.map(note => <li key={note._id}><Link to={`/notes/${note._id}`}>{note.title}</Link><button onClick={() => { this.handlePin(note._id) }}>Unpin</button><button onClick={() => {
                        this.handleDelete(note._id)
                    }}>delete</button><button onClick={() => {
                        this.handleCopy(note)}}>copy</button></li>)}
                </ul>

                <span><h2>others</h2></span>
                <ul>
                    {UnpinNotes.map(note => <li key={note._id}><Link to={`/notes/${note._id}`}>{note.title}</Link><button onClick={() => { this.handleUnPin(note._id) }}>pin</button><button onClick={() => {
                        const confirmRemove = window.confirm('Are you sure?')
                        if(confirmRemove){
                            this.handleDelete(note._id)
                        }
                    }}>delete</button><button onClick={() => {
                        this.handleCopy(note)}}>copy</button></li>)}
                </ul>

                <Link to='/notes/new'>Add Note</Link>
            </div>
        )
    }
}
export default NoteList
