import React from 'react'
import Axios from 'axios';


class Form extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            title:'',
            body:'',
            category:'',
            categories:[]
        }
        this.handleTitle=this.handleTitle.bind(this)
        this.handleBody=this.handleBody.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    handleTitle(e){
        const title=e.target.value
        this.setState(() => ({title}))
    }
    handleBody(e){
        const body=e.target.value
        this.setState(() => ({body}))
    }
    handleSubmit(e){
        e.preventDefault()
        const formData = {
            title:this.state.title,
            body:this.state.body,
            category:this.state.category
        }
        console.log(formData)
        this.props.handleSubmit(formData)

    }
    handleSelect = (e) => {
        const category = e.target.value
        this.setState(() => ({category}))
    }

    componentDidMount(){
        Axios.get('http://localhost:3006/categories')
        .then(response => {
                this.setState(() => ({categories:response.data}))
        })
    }
    componentWillReceiveProps(nextProps) {
        this.setState(() => ({
            title: nextProps.note.title,
            body: nextProps.note.body,
            category: nextProps.note.category._id
        }))
    }
    render(){
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        category:
                        <select value={this.state.category} name='category' onChange={this.handleSelect}>
                            <option value=''>select</option>
                            {this.state.categories.map(category => <option key={category._id} value={category._id}>{category.name}</option>)}
                            </select>
                    </label><br />

                    <label>
                        Title:
                        <input type='text' value={this.state.title} onChange={this.handleTitle}/> 
                    </label>
                    <br />
                    <label>
                        Body:
                        <input type='text' value={this.state.body} onChange={this.handleBody} />
                    </label><br />

                    <input type="submit" value="SUBMIT" />
                </form>
            </div>
        )
    }
}

export default Form
